/* ── Deterministic FNV-1a hash ─────────────────────────────────────── */
function fnv(str) {
  let h = 0x811c9dc5;
  for (let i = 0; i < str.length; i++) {
    h ^= str.charCodeAt(i);
    h = Math.imul(h, 0x01000193);
  }
  return h >>> 0;
}

function todayKey() {
  const d = new Date();
  return d.getFullYear() + "-" + (d.getMonth() + 1) + "-" + d.getDate();
}

/* ── Pick today's fish (only from entries with a pre-baked photo) ── */
const FISH_WITH_PHOTOS = FISH.filter(function (f) { return f[8] && f[8].indexOf("https://") === 0; });
let FISH_TODAY = FISH_WITH_PHOTOS[fnv(todayKey()) % FISH_WITH_PHOTOS.length];
let _fishDay = todayKey();

function checkFishDay() {
  const d = todayKey();
  if (d === _fishDay) return false;
  _fishDay = d;
  FISH_TODAY = FISH_WITH_PHOTOS[fnv(d) % FISH_WITH_PHOTOS.length];
  return true;
}

